def allPlugins = []
Jenkins.instance.pluginManager.plugins.each{
 plugin ->
   allPlugins << "${plugin.getShortName()}:${plugin.getVersion()}"
}
println allPlugins.sort().each {
    println it
}

def all_jobs = [
  "spotinst/spot-aws-ec2-market-predictions-v2/main",
]
for (job in all_jobs) {
  def props = Jenkins.instance.getItemByFullName(job).getProperty(ParametersDefinitionProperty.class)
  if (props != null) {
    def bntt_result = props.getParameterDefinitions().removeAll { 'BUILD_NUM_TO_TAG' == it.name }
    def dp_result   = props.getParameterDefinitions().removeAll { 'DEPLOY_PROD' == it.name }
    def bla_tests   = props.getParameterDefinitions().removeAll { 'ADD_TESTS_TO_RUN' == it.name }
    println "bntt_result: ${bntt_result}\ndp_result: ${dp_result}\nbla_tests: ${bla_tests}"
  }
}

// Groovy script to delete a Jenkins organization job with admin privileges
// This will delete the job: spotinst-mcs.h0l8bf.es-connector

import hudson.security.ACL

def orgName = 'spotinst'
def repoName = 'spotinst-mcs-network-es-connector'

ACL.impersonate(ACL.SYSTEM) {
    def orgFolder = Jenkins.instance.getItem(orgName)

    if (orgFolder != null) {
        def job = orgFolder.getItem(repoName)

        if (job != null) {
            job.delete()
            println "Job '${orgName}/${repoName}' has been successfully deleted."
        } else {
            println "Job '${repoName}' not found in organization '${orgName}'."
        }
    } else {
        println "Organization folder '${orgName}' not found."
    }
}

function fancy_print() {
  str=$1
  str_len=${#str}
  eval printf '=%.0s' {1..$((str_len + 6))}
  echo -e "\n== $str =="
  eval printf '=%.0s' {1..$((str_len + 6))}
  echo ""
}

